document.addEventListener('DOMContentLoaded', () => {
  const pointers = document.querySelectorAll('.pointer');
  
  pointers.forEach(pointer => {
    pointer.addEventListener('click', () => {
      // Remove active class from all pointers
      pointers.forEach(p => p.classList.remove('active'));
      
      // Add active class to the clicked pointer
      pointer.classList.add('active');
    });
  });
});


document.addEventListener('DOMContentLoaded', () => {
  const pointers = document.querySelectorAll('.pointer');
  const sections = document.querySelectorAll('.section');

  pointers.forEach(pointer => {
      pointer.addEventListener('click', () => {
          // Remove active class from all pointers
          pointers.forEach(p => p.classList.remove('active'));
          // Remove active class from all sections
          sections.forEach(s => s.classList.remove('active'));

          // Add active class to the clicked pointer
          pointer.classList.add('active');
          // Find the corresponding section to show
          const targetId = pointer.getAttribute('data-target');
          document.getElementById(targetId).classList.add('active');
      });
  });
});
